<script>

    function select(value) {
        var types=""""
        if (value != undefined) {
            types = value
        } else {
            types = $(""#types"").val()
        }
        var title = $(""input[name='title']"").val().trim()
        if (title != """" || types != 0) {
            $.ajax({
                type: 'get',
                url: ""/Home/GetBooks"",
                data: { ""title"": title, ""Types"": types },
                datatype: 'json',
                success: function (res) {
                    $("".Checklist"").html("""");
                    var dataHtml = """";
                    for (var i = 0; i < res.length; i++) {
                        dataHtml += '<div class=""card"" style=""width: 18rem;"">'
                        dataHtml += '<img src=""/img/' + res[i].imgUrl + '"" class=""card-img-top"" alt=""..."">'
                        dataHtml += '<div class=""card-body"">'
                        dataHtml += '<h5 class=""card-title"">' + res[i].title + '</h5>'
           ");
            WriteLiteral(@"             dataHtml += '<a href=""/Home/Seedetails?booksid=' + res[i].id + '"" class=""btn btn-primary"">点击查看详情</a></div> </div> '
                    }
                    if (res.length < 1) {
                        alert(""没有你要搜索的内容"")
                    }
                    $("".Checklist"").html(dataHtml);
                }, error: function (err) {
                    console.log(err)
                    alert(""请求异常"")
                }
            });
        }
    }


    $(""#v-pills-tab>a"").hover(function () {
        $(this).siblings().each(function () {
            $(this).removeClass(""active"")
        });
        $(this).addClass(""active"")
    });

    $(""a"").click(function () {
        var value = $(this).attr(""value"")
        select(value)
    })
</script>
